import { createContext, useReducer } from "react";

const WorkoutsContext = createContext();

function workoutsReducer(state, action) {
  switch (action.type) {
    case "SET_WORKOUTS":
      return {
        workouts: action.payload,
      };
    case "CREATE_WORKOUT":
      return {
        workouts: [action.payload, ...state.workouts],
      };
    case "DELETE_WORKOUT":
      const updatedWorkouts = state.workouts.filter(
        (workout) => workout._id !== action.payload.id
      );
      return {
        workouts: updatedWorkouts,
      };
    default:
      return state;
  }
}

function WorkoutsContextProvider({ children }) {
  const [state, dispatch] = useReducer(workoutsReducer, {
    workouts: null,
  });

  return (
    <WorkoutsContext.Provider value={{ ...state, dispatch }}>
      {children}
    </WorkoutsContext.Provider>
  );
}

export { WorkoutsContextProvider, WorkoutsContext, workoutsReducer };
