import { useAuthContext } from "./useAuthContext";

function useLogout() {
  const { dispatch } = useAuthContext();

  function logout() {
    //remove from localstorage
    localStorage.removeItem("user");

    //Set user to null in useAuthContext
    dispatch({ type: "LOGOUT" });
  }

  return { logout };
}
